function calculateChiSquare(observed, expected, total) {
  let chiSquare = 0;
  
  for (let i = 0; i < observed.length; i++) {
    const expectedCount = (expected[i] / 100) * total;
    if (expectedCount > 0) {
      chiSquare += Math.pow(observed[i] - expectedCount, 2) / expectedCount;
    }
  }
  
  return chiSquare;
}

